import telebot
import random
from telebot import types

token=''
bot=telebot.TeleBot(token)
@bot.message_handler(commands=['start'])
def start_message(message):
    bot.send_message(message.chat.id, "Привет. Меня зовут Ильшат. Я человек-оркестр и создал бота по своему образу и подобию.\n\n- Если хотите узнать обо мне больше, введите: /resume\n- Если хотите анекдот, введите: /lol\n- Если хотите подбросить монетку, введите: /orel\n - Если хотите предсказание, введите: /vanga\n")

@bot.message_handler(commands=['orel'])
def orel_reshka(message):
    a = random.randint(0, 1)
    if a == 0:
        bot.send_message(message.chat.id, 'Выпал "Орёл"')
    else:
        bot.send_message(message.chat.id, 'Выпала "Решка"')
        
