@bot.message_handler(commands=['resume'])
def button_message(message):
    markup=types.ReplyKeyboardMarkup(resize_keyboard=True)
    item1=types.KeyboardButton("Общая информация:")
    markup.add(item1)
    item2=types.KeyboardButton("Профессиональные навыки:")
    markup.add(item2)
    item3=types.KeyboardButton("Образование:")
    markup.add(item3)
    item4=types.KeyboardButton("Опыт работы:")
    markup.add(item4)
    item1=types.KeyboardButton("Контакты:")
    markup.add(item1)
    
    bot.send_message(message.chat.id,'Сейчас сможете узнать обо мне подробнее:',reply_markup=markup)
    
